import ExpenseItem from "./Components/Expense/ExpenseItems";
import React, { useState } from "react";
import "./Components/Expense/Expenses.css";
import Card from "./Components/UI/Card";
import Filter from "./Components/UI/Filter";
import NewExpense from "./Components/NewExpense/NewExpense";
function App() {
  const expenses = [
    {
      id: "1",
      title: "Toilet Paper",
      amount: 234.5,
      date: new Date(2021, 5, 23)
    },
    {
      id: "2",
      title: "Book",
      amount: 321.5,
      date: new Date(2021, 4, 13)
    },
    {
      id: "3",
      title: "Eggs",
      amount: 780.5,
      date: new Date(2021, 5, 29)
    },
    {
      id: "4",
      title: "Purses",
      amount: 322.5,
      date: new Date(2021, 2, 2)
    }
  ];
  const appExpenseHandler = (expense) => {
    expenses.push(expense);

  };

  const [filteredYear, setFilteredYear] = useState("2020")

  const filterHandler = (selectedYear) => {
    setFilteredYear(selectedYear)
  };

  return (
    <div>
      <NewExpense onAddExpense={appExpenseHandler }/>
      <Card className="expenses">
        <Filter selected={filteredYear} onChangeFilter={filterHandler} />
        {props.items.map(() => { <ExpenseItem title={expenses.title} amount="expe" />})};
        <ExpenseItem title={expenses[0].title} amount={expenses[0].amount} date={expenses[0].date}></ExpenseItem>
      <ExpenseItem title={expenses[1].title} amount={expenses[1].amount} date={expenses[1].date}></ExpenseItem>
      <ExpenseItem title={expenses[2].title} amount={expenses[2].amount} date={expenses[2].date}></ExpenseItem>
      <ExpenseItem title={expenses[3].title} amount={expenses[3].amount} date={expenses[3].date}></ExpenseItem>
      </Card>
    </div>
  );

}

export default App;
