import "./ExpenseItem.css";
function ExpenseItem(props) {
    const month = props.date.toLocaleString('en-US', { month: 'long' });
    const day= props.date.toLocaleString('en-US', { date: '2-digit' });
    const year = props.date.getFullYear();
    return (<div className="expense-item">
        <div>{month}</div>
        <div>{props.date.toLocaleString('en-US', { year: 'long' })}</div>
        <div>{props.date.toLocaleString('en-US', { date: 'long' })}</div>
        <div  className="expense-item__description"><h2>{props.title}</h2>
            <div className="expense-item__price">{props.amount}</div></div>
    </div>);

}
export default ExpenseItem;
