import ExpenseItem from "./Components/Expense/ExpenseItems";
import React, { useState } from "react";
import "./Components/Expense/Expenses.css";
import Card from "./Components/UI/Card";
import Filter from "./Components/UI/Filter";
import NewExpense from "./Components/NewExpense/NewExpense";
function App() {

  const [expenses, setExpenses] = useState(DUMMY_EXPENSES);
  const appExpenseHandler = (expense) => {
    setExpenses((prevExpense) => {
      return [expense, ...prevExpense];
    });
  };

  const [filteredYear, setFilteredYear] = useState("2020")

  const filterHandler = (selectedYear) => {
    setFilteredYear(selectedYear)
  };

  return (
    <div>
      <NewExpense onAddExpense={appExpenseHandler }/>
      <Card className="expenses">
        <Filter selected={filteredYear} onChangeFilter={filterHandler} />
        {expenses.map((item) => (<ExpenseItem title={item.title} amount={item.amount} date={item.date} />))};
      </Card>
    </div>
  );

}

export default App;
